import Downshift from "downshift";
import { useCallback, useEffect, useRef, useState } from "react";
import classNames from "classnames";
import {
  DropdownPosition,
  GetDropdownPositionFn,
  InputSelectOnChange,
  InputSelectProps,
} from "./types";

export function InputSelect<TItem>({
  label,
  defaultValue,
  onChange: consumerOnChange,
  items,
  parseItem,
  isLoading,
  loadingLabel,
}: InputSelectProps<TItem>) {
  const [selectedValue, setSelectedValue] = useState<TItem | null>(
    defaultValue ?? null
  );
  const [dropdownPosition, setDropdownPosition] = useState<DropdownPosition>({
    top: 0,
    left: 0,
  });
  const dropdownRef = useRef<HTMLDivElement>(null);

  const onChange = useCallback<InputSelectOnChange<TItem>>(
    (selectedItem) => {
      if (selectedItem === null) {
        return;
      }

      consumerOnChange(selectedItem);
      setSelectedValue(selectedItem);
    },
    [consumerOnChange]
  );

  const getDropdownPosition: GetDropdownPositionFn = (target) => {
    if (target instanceof Element) {
      const { top, left } = target.getBoundingClientRect();
      const { scrollY } = window;
      return {
        top: scrollY + top + target.clientHeight,
        left,
      };
    }

    return { top: 0, left: 0 };
  };

  useEffect(() => {
    const handleScroll = () => {
      if (dropdownRef.current) {
        const { top, left } = dropdownRef.current.getBoundingClientRect();
        const { scrollY } = window;
        setDropdownPosition({ top: top + scrollY, left });
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Downshift<TItem>
      id="KaizntreeSelect"
      onChange={onChange}
      selectedItem={selectedValue}
      itemToString={(item) => (item ? parseItem(item).label : "")}
    >
      {({
        getItemProps,
        getLabelProps,
        getMenuProps,
        isOpen,
        highlightedIndex,
        selectedItem,
        getToggleButtonProps,
        inputValue,
      }) => {
        const toggleProps = getToggleButtonProps();
        const parsedSelectedItem =
          selectedItem === null ? null : parseItem(selectedItem);

        return (
          <div className="KaizntreeInputSelect--root">
            <label
              className="KaizntreeText--s KaizntreeText--hushed"
              {...getLabelProps()}
            >
              {label}
            </label>
            <div className="KaizntreeBreak--xs" />
            <div
              className="KaizntreeInputSelect--input"
              onClick={(event) => {
                setDropdownPosition(getDropdownPosition(event.currentTarget));
                toggleProps.onClick(event);
              }}
            >
              {inputValue}
            </div>

            {isOpen && (
              <div
                ref={dropdownRef}
                className={classNames(
                  "KaizntreeInputSelect--dropdown-container",
                  {
                    "KaizntreeInputSelect--dropdown-container-opened": isOpen,
                  }
                )}
                {...getMenuProps()}
                style={{
                  top: dropdownPosition.top,
                  left: dropdownPosition.left,
                }}
              >
                {renderItems()}
              </div>
            )}
          </div>
        );

        function renderItems() {
          if (isLoading) {
            return (
              <div className="KaizntreeInputSelect--dropdown-item">
                {loadingLabel}...
              </div>
            );
          }

          if (items.length === 0) {
            return (
              <div className="KaizntreeInputSelect--dropdown-item">
                No items
              </div>
            );
          }

          return items.map((item, index) => {
            const parsedItem = parseItem(item);
            return (
              <div
                key={parsedItem.value}
                {...getItemProps({
                  key: parsedItem.value,
                  index,
                  item,
                  className: classNames("KaizntreeInputSelect--dropdown-item", {
                    "KaizntreeInputSelect--dropdown-item-highlighted":
                      highlightedIndex === index,
                    "KaizntreeInputSelect--dropdown-item-selected":
                      parsedSelectedItem?.value === parsedItem.value,
                  }),
                })}
              >
                {parsedItem.label}
              </div>
            );
          });
        }
      }}
    </Downshift>
  );
}
