// index.tsx

import { useCallback, useState } from "react"; // Added useState
import { useCustomFetch } from "src/hooks/useCustomFetch";
import { SetTransactionApprovalParams } from "src/utils/types";
import { TransactionPane } from "./TransactionPane";
import { SetTransactionApprovalFunction, TransactionsComponent } from "./types";
import { Transaction } from "src/utils/types";

export const Transactions: TransactionsComponent = ({
  transactions: initialTransactions,
}) => {
  const { fetchWithoutCache, loading } = useCustomFetch();
  const [transactions, setTransactions] = useState<Transaction[] | null>(
    initialTransactions
  ); // Use state to keep track of transactions

  const setTransactionApproval = useCallback<SetTransactionApprovalFunction>(
    async ({ transactionId, newValue }) => {
      await fetchWithoutCache<void, SetTransactionApprovalParams>(
        "setTransactionApproval",
        {
          transactionId,
          value: newValue,
        }
      );
    },
    [fetchWithoutCache]
  );

  if (transactions === null) {
    return <div className="KaizntreeLoading--container">Loading...</div>;
  }

  return (
    <div data-testid="transaction-container">
      {/* Use spread operator to include existing transactions */}
      {[...transactions].map((transaction) => (
        <TransactionPane
          key={transaction.id}
          transaction={transaction}
          loading={loading}
          setTransactionApproval={setTransactionApproval}
        />
      ))}
    </div>
  );
};
